// export default CalendarView
import React, { useState } from 'react';
import { timeSlots, days } from './timeSlots';

const TIME_COL_WIDTH = 90;
const SLOT_HEIGHT = 60;

const CalendarView = ({
  bookings = [],
  onBookingClick,
  onCellSelect
}) => {
  const [dragStart, setDragStart] = useState(null);

  /* =========================
     POSITION CALCULATIONS
  ========================== */
  const getTop = (time) => {
    const idx = timeSlots.indexOf(time);
    if (idx === -1) return null;
    return (idx + 1) * SLOT_HEIGHT;
  };

  const getHeight = (start, end) => {
    const startIdx = timeSlots.indexOf(start);
    const endIdx = timeSlots.indexOf(end);
    if (startIdx === -1 || endIdx === -1 || endIdx <= startIdx) {
      return null;
    }
    return (endIdx - startIdx) * SLOT_HEIGHT;
  };

  const getDayIndex = (day) =>
    days.findIndex(d => d.name === day);

  /* =========================
     GRID SELECTION (DRAG)
  ========================== */
  const handleMouseDown = (day, time) => {
    const nextSlot = timeSlots[timeSlots.indexOf(time) + 1];
    if (!nextSlot) return;

    setDragStart({ day, time });
    onCellSelect(day, time, nextSlot);
  };

  const handleMouseUp = (day, time) => {
    if (!dragStart) return;
    onCellSelect(dragStart.day, dragStart.time, time);
    setDragStart(null);
  };

  /* =========================
     RENDER
  ========================== */
  return (
    <div
      style={{
        position: 'relative',
        display: 'grid',
        gridTemplateColumns: `${TIME_COL_WIDTH}px repeat(${days.length}, 1fr)`,
        gridAutoRows: `${SLOT_HEIGHT}px`,
        width: '100%',
        border: '1px solid #ccc'
      }}
    >
      {/* EMPTY TOP LEFT */}
      <div />

      {/* DAY HEADERS */}
      {days.map(d => (
        <div
          key={d.name}
          style={{
            background: '#6a5acd',
            color: 'white',
            textAlign: 'center',
            padding: 6
          }}
        >
          <div>{d.name}</div>
          <small>{d.date}</small>
        </div>
      ))}

      {/* TIME GRID */}
      {timeSlots.map(time => (
        <React.Fragment key={time}>
          <div
            style={{
              borderRight: '1px solid #ccc',
              paddingLeft: 8,
              display: 'flex',
              alignItems: 'center',
              fontSize: 13
            }}
          >
            {time}
          </div>

          {days.map(d => (
            <div
              key={`${d.name}-${time}`}
              style={{
                border: '1px solid #eee',
                cursor: 'pointer'
              }}
              onMouseDown={() => handleMouseDown(d.name, time)}
              onMouseUp={() => handleMouseUp(d.name, time)}
            />
          ))}
        </React.Fragment>
      ))}

      {/* BOOKINGS */}
      {Array.isArray(bookings) &&
        bookings.map((b, i) => {
          const top = getTop(b.startTime);
          const height = getHeight(b.startTime, b.endTime);
          const dayIdx = getDayIndex(b.day);

          // ðŸš« Invalid booking â€” don't render
          if (top === null || height === null || dayIdx === -1) {
            console.warn('Invalid booking skipped:', b);
            return null;
          }

          return (
            <div
              key={b._id || i}
              onClick={() => onBookingClick(b)}
              style={{
                position: 'absolute',
                top,
                height,
                left: `calc(${TIME_COL_WIDTH}px + (${dayIdx} * ((100% - ${TIME_COL_WIDTH}px) / ${days.length})))`,
                width: `calc((100% - ${TIME_COL_WIDTH}px) / ${days.length})`,
                background: '#4caf50',
                color: 'white',
                padding: 8,
                borderRadius: 6,
                fontSize: 12,
                cursor: 'pointer',
                zIndex: 10
              }}
            >
              <strong>{b.startTime} â€“ {b.endTime}</strong>
              <div>{b.professor}</div>
            </div>
          );
        })}
    </div>
  );
};

export default CalendarView;
