import React, { useState } from 'react'
import CalendarView from './CalendarView'
import './Home.css'

const Home = () => {
  const [formData, setFormData] = useState({
    professor: '',
    department: '',
    school: '',
    day: '',
    startTime: '',
    endTime: '',
    room: '101'
  })

  const [bookings, setBookings] = useState([])
  const [selectedBooking, setSelectedBooking] = useState(null)

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value })

  const isOverlapping = (n) =>
    bookings.some(b =>
      b.day === n.day &&
      n.startTime < b.endTime &&
      n.endTime > b.startTime
    )

  const handleSubmit = (e) => {
    e.preventDefault()
    if (isOverlapping(formData)) {
      alert('Slot already booked')
      return
    }
    setBookings([...bookings, formData])
  }

  const handleCellSelect = (day, start, end) => {
    setFormData(prev => ({
      ...prev,
      day,
      startTime: start,
      endTime: end || prev.endTime,
      room: '101'
    }))
  }

  return (
    <>
      <div className="dashboard">
        <div className="card">
          <h3>Book Conference Room (101)</h3>

          <form onSubmit={handleSubmit} className="booking-form">
            <input name="professor" placeholder="Professor" onChange={handleChange} />
            <input name="department" placeholder="Department" onChange={handleChange} />
            <input name="school" placeholder="School" onChange={handleChange} />

            <input value="Room 101" disabled />

            <select name="day" value={formData.day} onChange={handleChange}>
              <option value="">Select Day</option>
              {['Monday','Tuesday','Wednesday','Thursday','Friday','Saturday','Sunday'].map(d =>
                <option key={d}>{d}</option>
              )}
            </select>

            <select name="startTime" value={formData.startTime} onChange={handleChange}>
              <option value="">Start Time</option>
              {['08:00','09:00','09:50','10:40','11:30','12:20','14:00','15:00','16:00'].map(t =>
                <option key={t}>{t}</option>
              )}
            </select>

            <select name="endTime" value={formData.endTime} onChange={handleChange}>
              <option value="">End Time</option>
              {['09:00','09:50','10:40','11:30','12:20','14:00','15:00','16:00','17:00'].map(t =>
                <option key={t}>{t}</option>
              )}
            </select>

            <button type="submit">Confirm Booking</button>
          </form>
        </div>

        <div className="card">
          <h3>Weekly Calendar</h3>
          <CalendarView
            bookings={bookings}
            onBookingClick={setSelectedBooking}
            onCellSelect={handleCellSelect}
          />
        </div>
      </div>

      {selectedBooking && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Meeting Details</h3>
            <p><b>Professor:</b> {selectedBooking.professor}</p>
            <p><b>Department:</b> {selectedBooking.department}</p>
            <p><b>School:</b> {selectedBooking.school}</p>
            <p><b>Room:</b> 101</p>
            <p><b>Day:</b> {selectedBooking.day}</p>
            <p><b>Time:</b> {selectedBooking.startTime} – {selectedBooking.endTime}</p>
            <button onClick={() => setSelectedBooking(null)}>Close</button>
          </div>
        </div>
      )}
    </>
  )
}

export default Home
