import React from 'react'
import { timeSlots, days } from './timeSlots'

const TIME_COL_WIDTH = 90
const SLOT_HEIGHT = 60

const CalendarView = ({ bookings = [], onBookingClick }) => {

  const getTop = (time) =>
    (timeSlots.indexOf(time) + 1) * SLOT_HEIGHT

  const getHeight = (start, end) =>
    (timeSlots.indexOf(end) - timeSlots.indexOf(start)) * SLOT_HEIGHT

  const getDayIndex = (dayName) =>
    days.findIndex(d => d.name === dayName)

  return (
    <div
      style={{
        position: 'relative',
        display: 'grid',
        gridTemplateColumns: `${TIME_COL_WIDTH}px repeat(${days.length}, 1fr)`,
        gridAutoRows: `${SLOT_HEIGHT}px`,
        width: '100%',
        border: '1px solid #ccc'
      }}
    >
      {/* EMPTY TOP-LEFT */}
      <div />

      {/* DAY HEADERS */}
      {days.map(d => (
        <div
          key={d.name}
          style={{
            background: '#6a5acd',
            color: 'white',
            textAlign: 'center',
            padding: '6px',
            fontWeight: 600
          }}
        >
          <div>{d.name}</div>
          <small style={{ fontWeight: 400 }}>{d.date}</small>
        </div>
      ))}

      {/* TIME + GRID */}
      {timeSlots.map(time => (
        <React.Fragment key={time}>
          {/* TIME COLUMN */}
          <div
            style={{
              borderRight: '1px solid #ccc',
              paddingLeft: '8px',
              display: 'flex',
              alignItems: 'center',
              fontSize: '13px'
            }}
          >
            {time}
          </div>

          {/* DAY CELLS */}
          {days.map(d => (
            <div
              key={`${d.name}-${time}`}
              style={{ border: '1px solid #eee' }}
            />
          ))}
        </React.Fragment>
      ))}

      {/* BOOKINGS */}
      {bookings.map((b, index) => (
        <div
          key={index}
          onClick={() => onBookingClick(b)}
          style={{
            position: 'absolute',
            top: getTop(b.startTime),
            height: getHeight(b.startTime, b.endTime),

            left: `calc(${TIME_COL_WIDTH}px + (${getDayIndex(b.day)} * ((100% - ${TIME_COL_WIDTH}px) / ${days.length})))`,
            width: `calc((100% - ${TIME_COL_WIDTH}px) / ${days.length})`,

            background: '#6fb33f',
            color: 'white',
            padding: '8px',
            borderRadius: '6px',
            fontSize: '12px',
            cursor: 'pointer',
            zIndex: 10
          }}
        >
          <strong>{b.startTime} â€“ {b.endTime}</strong>
          <div>{b.professor}</div>
          <div>{b.department}</div>
          <div>{b.room}</div>
        </div>
      ))}
    </div>
  )
}

export default CalendarView
