import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { handleSuccess, handleError } from '../utils'
import './Home.css'

const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday']

const timeSlots = [
  '08:00', '09:00', '09:50', '10:40',
  '11:30', '12:20', '13:10',
  '14:00', '15:00', '16:00', '17:00'
]

const rooms = ['Room 101', 'Room 102', 'Room 201', 'Room 202', 'Conference Hall']

const Home = () => {
  const navigate = useNavigate()
  const [loggedInUser, setLoggedInUser] = useState('')
  const [bookings, setBookings] = useState([])
  const [loading, setLoading] = useState(false)
  const [errors, setErrors] = useState({})

  const [formData, setFormData] = useState({
    professor: '',
    department: '',
    school: '',
    room: '',
    day: '',
    startTime: '',
    endTime: ''
  })

  useEffect(() => {
    const user = localStorage.getItem('loggedInUser')
    if (!user) {
      navigate('/login')
    } else {
      setLoggedInUser(user)
    }
  }, [navigate])

  // Fetch bookings from backend
  useEffect(() => {
    fetchBookings()
  }, [])

  const fetchBookings = async () => {
    try {
      const token = localStorage.getItem('token')
      if (!token) return
      
      const response = await fetch('http://localhost:8080/api/bookings', {
        headers: {
          'Authorization': token
        }
      })
      
      if (response.ok) {
        const data = await response.json()
        setBookings(data)
      }
    } catch (error) {
      console.error('Error fetching bookings:', error)
    }
  }

  const handleLogout = () => {
    localStorage.removeItem('token')
    localStorage.removeItem('loggedInUser')
    handleSuccess('User Logged Out')
    setTimeout(() => navigate('/login'), 1000)
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })
    
    // Clear error for this field
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' })
    }
  }

  const validateForm = () => {
    const newErrors = {}
    
    if (!formData.professor.trim()) {
      newErrors.professor = 'Professor name is required'
    }
    
    if (!formData.department.trim()) {
      newErrors.department = 'Department is required'
    }
    
    if (!formData.school.trim()) {
      newErrors.school = 'School is required'
    }
    
    if (!formData.room) {
      newErrors.room = 'Room selection is required'
    }
    
    if (!formData.day) {
      newErrors.day = 'Day selection is required'
    }
    
    if (!formData.startTime) {
      newErrors.startTime = 'Start time is required'
    }
    
    if (!formData.endTime) {
      newErrors.endTime = 'End time is required'
    }
    
    // Validate time logic
    if (formData.startTime && formData.endTime) {
      const startIdx = timeSlots.indexOf(formData.startTime)
      const endIdx = timeSlots.indexOf(formData.endTime)
      
      if (endIdx <= startIdx) {
        newErrors.endTime = 'End time must be after start time'
      }
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const checkBookingConflict = (newBooking) => {
    return bookings.some(booking => {
      // Check if same room and same day
      if (booking.room !== newBooking.room || booking.day !== newBooking.day) {
        return false
      }

      const newStart = timeSlots.indexOf(newBooking.startTime)
      const newEnd = timeSlots.indexOf(newBooking.endTime)
      const existingStart = timeSlots.indexOf(booking.startTime)
      const existingEnd = timeSlots.indexOf(booking.endTime)

      // Check for overlap
      return (newStart < existingEnd && newEnd > existingStart)
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    
    // Validate form
    if (!validateForm()) {
      handleError('Please fill in all required fields')
      return
    }
    
    // Check for conflicts
    if (checkBookingConflict(formData)) {
      handleError('This room is already booked for the selected time slot')
      setErrors({ ...errors, conflict: 'Booking conflict detected' })
      return
    }
    
    setLoading(true)
    
    try {
      const token = localStorage.getItem('token')
      if (!token) {
        handleError('Authentication required')
        navigate('/login')
        return
      }
      
      const response = await fetch('http://localhost:8080/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': token
        },
        body: JSON.stringify(formData)
      })
      
      const result = await response.json()
      
      if (response.ok) {
        handleSuccess('Booking created successfully!')
        setBookings([...bookings, result])
        
        // Reset form
        setFormData({
          professor: '',
          department: '',
          school: '',
          room: '',
          day: '',
          startTime: '',
          endTime: ''
        })
        setErrors({})
      } else {
        handleError(result.message || 'Failed to create booking')
      }
    } catch (error) {
      handleError('Error creating booking. Please try again.')
      console.error('Booking error:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleDeleteBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to delete this booking?')) {
      return
    }
    
    try {
      const token = localStorage.getItem('token')
      if (!token) {
        handleError('Authentication required')
        navigate('/login')
        return
      }
      
      const response = await fetch(`http://localhost:8080/api/bookings/${bookingId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': token
        }
      })
      
      if (response.ok) {
        handleSuccess('Booking deleted successfully')
        setBookings(bookings.filter(b => b._id !== bookingId))
      } else {
        handleError('Failed to delete booking')
      }
    } catch (error) {
      handleError('Error deleting booking')
      console.error('Delete error:', error)
    }
  }

  const getRow = (time) => timeSlots.indexOf(time) + 2
  const getCol = (day) => days.indexOf(day) + 2

  return (
    <>
      {/* NAVBAR */}
      <div className="navbar">
        <span className="nav-user">Welcome {loggedInUser}</span>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      <div className="home-content">
        <h2>Conference Room Booking</h2>

        {/* BOOKING FORM */}
        <div className="booking-form-container">
          <div className="booking-form">
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="professor">Professor Name *</label>
                <input
                  id="professor"
                  name="professor"
                  placeholder="Enter professor name"
                  value={formData.professor}
                  onChange={handleChange}
                  className={errors.professor ? 'error' : ''}
                />
                {errors.professor && <span className="error-text">{errors.professor}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="department">Department *</label>
                <input
                  id="department"
                  name="department"
                  placeholder="Enter department"
                  value={formData.department}
                  onChange={handleChange}
                  className={errors.department ? 'error' : ''}
                />
                {errors.department && <span className="error-text">{errors.department}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="school">School *</label>
                <input
                  id="school"
                  name="school"
                  placeholder="Enter school name"
                  value={formData.school}
                  onChange={handleChange}
                  className={errors.school ? 'error' : ''}
                />
                {errors.school && <span className="error-text">{errors.school}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="room">Conference Room *</label>
                <select
                  id="room"
                  name="room"
                  value={formData.room}
                  onChange={handleChange}
                  className={errors.room ? 'error' : ''}
                >
                  <option value="">Select Room</option>
                  {rooms.map(r => <option key={r} value={r}>{r}</option>)}
                </select>
                {errors.room && <span className="error-text">{errors.room}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="day">Day *</label>
                <select
                  id="day"
                  name="day"
                  value={formData.day}
                  onChange={handleChange}
                  className={errors.day ? 'error' : ''}
                >
                  <option value="">Select Day</option>
                  {days.map(d => <option key={d} value={d}>{d}</option>)}
                </select>
                {errors.day && <span className="error-text">{errors.day}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="startTime">Start Time *</label>
                <select
                  id="startTime"
                  name="startTime"
                  value={formData.startTime}
                  onChange={handleChange}
                  className={errors.startTime ? 'error' : ''}
                >
                  <option value="">Select Start Time</option>
                  {timeSlots.map(t => <option key={t} value={t}>{t}</option>)}
                </select>
                {errors.startTime && <span className="error-text">{errors.startTime}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="endTime">End Time *</label>
                <select
                  id="endTime"
                  name="endTime"
                  value={formData.endTime}
                  onChange={handleChange}
                  className={errors.endTime ? 'error' : ''}
                >
                  <option value="">Select End Time</option>
                  {timeSlots.map(t => <option key={t} value={t}>{t}</option>)}
                </select>
                {errors.endTime && <span className="error-text">{errors.endTime}</span>}
              </div>
            </div>

            {errors.conflict && (
              <div className="conflict-alert">
                ‚ö†Ô∏è {errors.conflict}
              </div>
            )}

            <button 
              type="button" 
              onClick={handleSubmit} 
              className="submit-btn"
              disabled={loading}
            >
              {loading ? 'Booking...' : 'Book Slot'}
            </button>
          </div>
        </div>

        {/* CALENDAR */}
        <div className="calendar-section">
          <h3>Weekly Schedule</h3>
          <div className="calendar-grid">
            <div></div>

            {days.map(day => (
              <div key={day} className="day-header">{day}</div>
            ))}

            {timeSlots.map(time => (
              <React.Fragment key={time}>
                <div className="time-label">{time}</div>
                {days.map(day => <div key={day + time} className="cell"></div>)}
              </React.Fragment>
            ))}

            {bookings.map((b, i) => (
              <div
                key={b._id || i}
                className="booking"
                style={{
                  gridRow: `${getRow(b.startTime)} / ${getRow(b.endTime)}`,
                  gridColumn: getCol(b.day)
                }}
              >
                <div className="booking-header">
                  <strong>{b.startTime} - {b.endTime}</strong>
                  <button 
                    className="delete-btn"
                    onClick={() => handleDeleteBooking(b._id)}
                    title="Delete booking"
                  >
                    √ó
                  </button>
                </div>
                <div className="booking-info">
                  <div>üë§ {b.professor}</div>
                  <div>üè¢ {b.department}</div>
                  <div>üìç {b.room}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  )
}

export default Home
