import React from 'react'
import { timeSlots, days } from './timeSlots'

const colors = ['#6a5acd', '#20b2aa', '#ff7f50', '#3cb371', '#ff6347']

const styles = {
  calendarGrid: {
    display: 'grid',
    gridTemplateColumns: '90px repeat(5, 1fr)',
    gridAutoRows: '60px',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    marginTop: '20px'
  },
  dayHeader: (index) => ({
    background: `linear-gradient(135deg, ${colors[index]}, #8a7cfb)`,
    color: 'white',
    fontWeight: '600',
    textAlign: 'center',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  }),
  timeLabel: {
    background: '#f8fafc',
    fontSize: '13px',
    fontWeight: '500',
    paddingLeft: '10px',
    display: 'flex',
    alignItems: 'center',
    borderRight: '1px solid #e5e7eb'
  },
  cell: {
    border: '1px solid #e5e7eb',
    background: '#ffffff'
  },
  booking: (index) => ({
    background: colors[index % colors.length],
    color: 'white',
    padding: '8px',
    borderRadius: '8px',
    fontSize: '12px',
    zIndex: 2,
    boxShadow: '0 4px 10px rgba(0,0,0,0.2)'
  })
}

const CalendarView = ({ bookings = [] }) => {

  const getRow = (time) => timeSlots.indexOf(time) + 2
  const getCol = (day) => days.indexOf(day) + 2

  return (
    <div style={styles.calendarGrid}>

      <div style={{ background: '#eef2f7' }}></div>

      {days.map((day, i) => (
        <div key={day} style={styles.dayHeader(i)}>
          {day}
        </div>
      ))}

      {timeSlots.map(time => (
        <React.Fragment key={time}>
          <div style={styles.timeLabel}>{time}</div>
          {days.map(day => (
            <div key={`${day}-${time}`} style={styles.cell}></div>
          ))}
        </React.Fragment>
      ))}

      {bookings.map((b, i) => (
        <div
          key={i}
          style={{
            ...styles.booking(i),
            gridRow: `${getRow(b.startTime)} / ${getRow(b.endTime)}`,
            gridColumn: getCol(b.day)
          }}
        >
          <strong>{b.startTime} â€“ {b.endTime}</strong>
          <div><b>{b.professor}</b></div>
          <div>{b.department}</div>
          <div>{b.room}</div>
        </div>
      ))}
    </div>
  )
}

export default CalendarView
