import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { handleSuccess } from '../utils'
import './Home.css'

const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday']

const timeSlots = [
  '08:00', '09:00', '09:50', '10:40',
  '11:30', '12:20', '13:10',
  '14:00', '15:00', '16:00', '17:00'
]

const Home = () => {
  const navigate = useNavigate()
  const [loggedInUser, setLoggedInUser] = useState('')

  const [formData, setFormData] = useState({
    professor: '',
    department: '',
    school: '',
    room: '',
    day: '',
    startTime: '',
    endTime: ''
  })

  const [bookings, setBookings] = useState([])

  useEffect(() => {
    setLoggedInUser(localStorage.getItem('handleLogout'))
  }, [])

  const handleLogout = () => {
    localStorage.removeItem('token')
    localStorage.removeItem('handleLogout')
    handleSuccess('User Logged Out')
    setTimeout(() => navigate('/login'), 1000)
  }

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    setBookings([...bookings, formData])
    setFormData({
      professor: '',
      department: '',
      school: '',
      room: '',
      day: '',
      startTime: '',
      endTime: ''
    })
  }

  const getRow = (time) => timeSlots.indexOf(time) + 2
  const getCol = (day) => days.indexOf(day) + 2

  return (
    <>
      {/* NAVBAR */}
      <div className="navbar">
        <span className="nav-user">Welcome {loggedInUser}</span>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      <div className="home-content">

        <h2>Conference Room Booking</h2>

        {/* BOOKING FORM */}
        <form className="booking-form" onSubmit={handleSubmit}>
          <input name="professor" placeholder="Professor Name" value={formData.professor} onChange={handleChange} />
          <input name="department" placeholder="Department" value={formData.department} onChange={handleChange} />
          <input name="school" placeholder="School" value={formData.school} onChange={handleChange} />
          <input name="room" placeholder="Room No" value={formData.room} onChange={handleChange} />

          <select name="day" value={formData.day} onChange={handleChange}>
            <option value="">Select Day</option>
            {days.map(d => <option key={d}>{d}</option>)}
          </select>

          <select name="startTime" value={formData.startTime} onChange={handleChange}>
            <option value="">Start Time</option>
            {timeSlots.map(t => <option key={t}>{t}</option>)}
          </select>

          <select name="endTime" value={formData.endTime} onChange={handleChange}>
            <option value="">End Time</option>
            {timeSlots.map(t => <option key={t}>{t}</option>)}
          </select>

          <button type="submit">Book Slot</button>
        </form>

        {/* CALENDAR */}
        <div className="calendar-grid">
          <div></div>

          {days.map(day => (
            <div key={day} className="day-header">{day}</div>
          ))}

          {timeSlots.map(time => (
            <React.Fragment key={time}>
              <div className="time-label">{time}</div>
              {days.map(day => <div key={day + time} className="cell"></div>)}
            </React.Fragment>
          ))}

          {bookings.map((b, i) => (
            <div
              key={i}
              className="booking"
              style={{
                gridRow: `${getRow(b.startTime)} / ${getRow(b.endTime)}`,
                gridColumn: getCol(b.day)
              }}
            >
              <strong>{b.startTime} - {b.endTime}</strong>
              <div>{b.professor}</div>
              <div>{b.department}</div>
              <div>{b.room}</div>
            </div>
          ))}
        </div>

      </div>
    </>
  )
}

export default Home
