import React from 'react'
import { timeSlots, days } from './timeSlots'

const TIME_COL_WIDTH = 90
const SLOT_HEIGHT = 60

const CalendarView = ({ bookings = [], onBookingClick }) => {

  const getTop = (time) =>
    (timeSlots.indexOf(time) + 1) * SLOT_HEIGHT

  const getHeight = (start, end) =>
    (timeSlots.indexOf(end) - timeSlots.indexOf(start)) * SLOT_HEIGHT

  return (
    <div
      style={{
        position: 'relative',
        display: 'grid',
        gridTemplateColumns: `${TIME_COL_WIDTH}px repeat(${days.length}, 1fr)`,
        gridAutoRows: `${SLOT_HEIGHT}px`,
        border: '1px solid #ddd',
        width: '100%'
      }}
    >
      {/* EMPTY TOP-LEFT */}
      <div></div>

      {/* DAY HEADERS */}
      {days.map(day => (
        <div
          key={day.name}
          style={{
            background: '#6a5acd',
            color: 'white',
            fontWeight: '600',
            textAlign: 'center',
            padding: '6px',
            borderLeft: '1px solid #ddd'
          }}
        >
          <div>{day.name}</div>
          <small style={{ fontWeight: '400' }}>{day.date}</small>
        </div>
      ))}

      {/* TIME + GRID CELLS */}
      {timeSlots.map(time => (
        <React.Fragment key={time}>
          {/* TIME COLUMN */}
          <div
            style={{
              background: '#f8fafc',
              paddingLeft: '10px',
              display: 'flex',
              alignItems: 'center',
              borderRight: '1px solid #ddd',
              fontSize: '13px'
            }}
          >
            {time}
          </div>

          {/* DAY CELLS */}
          {days.map(day => (
            <div
              key={`${day.name}-${time}`}
              style={{ border: '1px solid #eee' }}
            />
          ))}
        </React.Fragment>
      ))}

      {/* BOOKINGS */}
      {bookings.map((b, index) => (
        <div
          key={index}
          onClick={() => onBookingClick && onBookingClick(b)}
          style={{
            position: 'absolute',
            top: getTop(b.startTime),
            height: getHeight(b.startTime, b.endTime),

            left: `calc(${TIME_COL_WIDTH}px + (${days.findIndex(d => d.name === b.day)} * ((100% - ${TIME_COL_WIDTH}px) / ${days.length})))`,
            width: `calc((100% - ${TIME_COL_WIDTH}px) / ${days.length})`,

            background: '#6fb33f',
            color: 'white',
            padding: '8px',
            borderRadius: '8px',
            fontSize: '12px',
            boxSizing: 'border-box',
            cursor: 'pointer',
            zIndex: 10
          }}
        >
          <strong>{b.startTime} â€“ {b.endTime}</strong>
          <div>{b.professor}</div>
          <div>{b.department}</div>
          <div>{b.room}</div>
        </div>
      ))}
    </div>
  )
}

export default CalendarView
