  try {
    const bookings = await Booking.find().sort({ createdAt: -1 });
    res.status(200).json(bookings);
  } catch (error) {
    res.status(500).json({
      message: 'Error fetching bookings',
      error: error.message
    });
  }
});

// Get bookings for a specific user
router.get('/my-bookings', ensureAuthenticated, async (req, res) => {
  try {
    const bookings = await Booking.find({ userId: req.user._id })
      .sort({ createdAt: -1 });
    res.status(200).json(bookings);
  } catch (error) {
    res.status(500).json({
      message: 'Error fetching your bookings',
      error: error.message
    });
  }
});

// Create a new booking
router.post('/', ensureAuthenticated, async (req, res) => {
  try {
    const { professor, department, school, room, day, startTime, endTime } = req.body;

    // Validation
    if (!professor || !department || !school || !room || !day || !startTime || !endTime) {
      return res.status(400).json({
        message: 'All fields are required',
        success: false
      });
    }

    // Check for conflicting bookings
    const existingBooking = await Booking.findOne({
      room: room,
      day: day,
      $or: [
        {
          $and: [
            { startTime: { $lte: startTime } },
            { endTime: { $gt: startTime } }
          ]
        },
        {
          $and: [
            { startTime: { $lt: endTime } },
            { endTime: { $gte: endTime } }
          ]
        },
        {
          $and: [
            { startTime: { $gte: startTime } },
            { endTime: { $lte: endTime } }
          ]
        }
      ]
    });

    if (existingBooking) {
      return res.status(409).json({
        message: 'This room is already booked for the selected time slot',
        success: false
      });
    }

    // Create new booking
    const newBooking = new Booking({
      professor,
      department,
      school,
      room,
      day,
      startTime,
      endTime,
      userId: req.user._id,
      userName: req.user.name
    });

    await newBooking.save();

    res.status(201).json({
      message: 'Booking created successfully',
      success: true,
      ...newBooking.toObject()
    });
  } catch (error) {
    res.status(500).json({
      message: 'Error creating booking',
      error: error.message,
      success: false
    });
  }
});

// Update a booking
router.put('/:id', ensureAuthenticated, async (req, res) => {
  try {
    const { id } = req.params;
    const { professor, department, school, room, day, startTime, endTime } = req.body;

    const booking = await Booking.findById(id);

    if (!booking) {
      return res.status(404).json({
        message: 'Booking not found',
        success: false
      });
    }

    // Check if user owns this booking (optional - remove if any user can edit)
    if (booking.userId.toString() !== req.user._id.toString()) {
      return res.status(403).json({
        message: 'You are not authorized to update this booking',
        success: false
      });
    }

    // Check for conflicts with other bookings (excluding current booking)
    const conflictingBooking = await Booking.findOne({
      _id: { $ne: id },
      room: room,
      day: day,
      $or: [
        {
          $and: [
            { startTime: { $lte: startTime } },
            { endTime: { $gt: startTime } }
          ]
        },
        {
          $and: [
            { startTime: { $lt: endTime } },
            { endTime: { $gte: endTime } }
          ]
        },
        {
          $and: [
            { startTime: { $gte: startTime } },
            { endTime: { $lte: endTime } }
          ]
        }
      ]
    });

    if (conflictingBooking) {
      return res.status(409).json({
        message: 'This room is already booked for the selected time slot',
        success: false
      });
    }

    // Update booking
    booking.professor = professor;
    booking.department = department;
    booking.school = school;
    booking.room = room;
    booking.day = day;
    booking.startTime = startTime;
    booking.endTime = endTime;

    await booking.save();

    res.status(200).json({
      message: 'Booking updated successfully',
      success: true,
      ...booking.toObject()
    });
  } catch (error) {
    res.status(500).json({
      message: 'Error updating booking',
      error: error.message,
      success: false
    });
  }
});

// Delete a booking
router.delete('/:id', ensureAuthenticated, async (req, res) => {
  try {
    const { id } = req.params;

    const booking = await Booking.findById(id);

    if (!booking) {
      return res.status(404).json({
        message: 'Booking not found',
        success: false
      });
    }

    // Check if user owns this booking (optional - remove if any user can delete)
    if (booking.userId.toString() !== req.user._id.toString()) {
      return res.status(403).json({
        message: 'You are not authorized to delete this booking',
        success: false
      });
    }

    await Booking.findByIdAndDelete(id);

    res.status(200).json({
      message: 'Booking deleted successfully',
      success: true
    });
  } catch (error) {
    res.status(500).json({
      message: 'Error deleting booking',
      error: error.message,
      success: false
    });
  }
});

module.exports = router;
