import React from 'react'
import { timeSlots, days } from './timeSlots'

const TIME_COL_WIDTH = 90   // must match time column width
const SLOT_HEIGHT = 60      // must match row height

const CalendarView = ({ bookings = [] }) => {

  const getTop = (time) =>
    (timeSlots.indexOf(time) + 1) * SLOT_HEIGHT

  const getHeight = (start, end) =>
    (timeSlots.indexOf(end) - timeSlots.indexOf(start)) * SLOT_HEIGHT

  return (
    <div
      style={{
        position: 'relative',
        display: 'grid',
        gridTemplateColumns: `${TIME_COL_WIDTH}px repeat(5, 1fr)`,
        gridAutoRows: `${SLOT_HEIGHT}px`,
        border: '1px solid #ddd'
      }}
    >
      {/* EMPTY TOP-LEFT */}
      <div></div>

      {/* DAY HEADERS */}
      {days.map(day => (
        <div
          key={day}
          style={{
            background: '#6a5acd',
            color: 'white',
            fontWeight: '600',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          {day}
        </div>
      ))}

      {/* TIME + CELLS */}
      {timeSlots.map(time => (
        <React.Fragment key={time}>
          <div
            style={{
              background: '#f8fafc',
              paddingLeft: '10px',
              display: 'flex',
              alignItems: 'center',
              borderRight: '1px solid #ddd'
            }}
          >
            {time}
          </div>

          {days.map(day => (
            <div
              key={`${day}-${time}`}
              style={{ border: '1px solid #eee' }}
            ></div>
          ))}
        </React.Fragment>
      ))}

      {/* BOOKINGS (ABSOLUTE OVERLAY) */}
      {bookings.map((b, index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            top: getTop(b.startTime),
            height: getHeight(b.startTime, b.endTime),

            /* ðŸ”¥ IMPORTANT FIX */
            left: `calc(${TIME_COL_WIDTH}px + (${days.indexOf(b.day)} * ((100% - ${TIME_COL_WIDTH}px) / ${days.length})))`,
            width: `calc((100% - ${TIME_COL_WIDTH}px) / ${days.length})`,

            background: '#6fb33f',
            color: 'white',
            padding: '8px',
            borderRadius: '8px',
            fontSize: '12px',
            boxSizing: 'border-box',
            zIndex: 10
          }}
        >
          <strong>{b.startTime} â€“ {b.endTime}</strong>
          <div>{b.professor}</div>
          <div>{b.department}</div>
          <div>{b.room}</div>
        </div>
      ))}
    </div>
  )
}

export default CalendarView
