import React from 'react'
import { timeSlots, days } from './timeSlots'

const SLOT_HEIGHT = 60 // must match row height

const styles = {
  calendarGrid: {
    display: 'grid',
    gridTemplateColumns: '90px repeat(5, 1fr)',
    position: 'relative',
    border: '1px solid #ddd',
    marginTop: '20px'
  },
  gridBody: {
    display: 'grid',
    gridTemplateColumns: '90px repeat(5, 1fr)',
    gridAutoRows: `${SLOT_HEIGHT}px`
  },
  dayHeader: {
    background: '#6a5acd',
    color: 'white',
    fontWeight: '600',
    textAlign: 'center',
    padding: '12px 0'
  },
  timeLabel: {
    background: '#f8fafc',
    paddingLeft: '10px',
    fontSize: '13px',
    display: 'flex',
    alignItems: 'center',
    borderRight: '1px solid #e5e7eb'
  },
  cell: {
    border: '1px solid #e5e7eb'
  },
  booking: {
    position: 'absolute',
    left: 0,
    padding: '8px',
    borderRadius: '8px',
    background: '#6a5acd',
    color: 'white',
    fontSize: '12px',
    boxShadow: '0 4px 10px rgba(0,0,0,0.25)'
  }
}

const CalendarView = ({ bookings = [] }) => {

  const getTop = (time) =>
    (timeSlots.indexOf(time) + 1) * SLOT_HEIGHT + SLOT_HEIGHT

  const getHeight = (start, end) =>
    (timeSlots.indexOf(end) - timeSlots.indexOf(start)) * SLOT_HEIGHT

  const getLeft = (day) =>
    90 + days.indexOf(day) * (100 / days.length) + '%'

  const getWidth = () => `${100 / days.length}%`

  return (
    <div style={styles.calendarGrid}>

      {/* HEADER */}
      <div style={styles.gridBody}>
        <div></div>
        {days.map(day => (
          <div key={day} style={styles.dayHeader}>{day}</div>
        ))}

        {timeSlots.map(time => (
          <React.Fragment key={time}>
            <div style={styles.timeLabel}>{time}</div>
            {days.map(day => (
              <div key={`${day}-${time}`} style={styles.cell}></div>
            ))}
          </React.Fragment>
        ))}
      </div>

      {/* BOOKINGS OVERLAY */}
      {bookings.map((b, i) => (
        <div
          key={i}
          style={{
            ...styles.booking,
            top: getTop(b.startTime),
            height: getHeight(b.startTime, b.endTime),
            left: `calc(90px + ${days.indexOf(b.day)} * ((100% - 90px) / ${days.length}))`,
            width: `calc((100% - 90px) / ${days.length})`
          }}
        > 
          <strong>{b.startTime} â€“ {b.endTime}</strong>
          <div>{b.professor}</div>
          <div>{b.department}</div>
          <div>{b.room}</div>
        </div>
      ))}
    </div>
  )
}

export default CalendarView
