import React from 'react'
import { timeSlots, days } from './timeSlots'
import 'Calendar.css'

const CalendarView = ({ bookings }) => {

  const getRowIndex = (time) => timeSlots.indexOf(time) + 2
  const getColIndex = (day) => days.indexOf(day) + 2

  return (
    <div className="calendar-grid">

      {/* Empty top-left */}
      <div></div>

      {/* Days header */}
      {days.map(day => (
        <div key={day} className="day-header">{day}</div>
      ))}

      {/* Time labels */}
      {timeSlots.map(time => (
        <React.Fragment key={time}>
          <div className="time-label">{time}</div>
          {days.map(day => (
            <div key={day + time} className="cell"></div>
          ))}
        </React.Fragment>
      ))}

      {/* Bookings */}
      {bookings.map((b, index) => (
        <div
          key={index}
          className="booking"
          style={{
            gridRow: `${getRowIndex(b.startTime)} / ${getRowIndex(b.endTime)}`,
            gridColumn: getColIndex(b.day)
          }}
        >
          <strong>{b.startTime} - {b.endTime}</strong>
          <p>{b.professor}</p>
          <p>{b.department}</p>
          <p>{b.room}</p>
        </div>
      ))}

    </div>
  )
}

export default CalendarView
