import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import CalendarView from './CalendarView'
import { handleSuccess } from '../utils'
import './Home.css'

const Home = () => {
  const navigate = useNavigate()

  const [loggedInUser, setLoggedInUser] = useState('')
  const [bookings, setBookings] = useState([])
  const [selectedBooking, setSelectedBooking] = useState(null)

  const [formData, setFormData] = useState({
    professor: '',
    department: '',
    school: '',
    day: '',
    startTime: '',
    endTime: '',
    room: '101'
  })

  /* ---------------- LOAD USER ---------------- */
  useEffect(() => {
    setLoggedInUser(localStorage.getItem('username'))
  }, [])

  /* ---------------- LOGOUT ---------------- */
  const handleLogout = () => {
    localStorage.clear()
    handleSuccess('Logged out')
    navigate('/login')
  }

  /* ---------------- FORM HANDLERS ---------------- */
  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value })

  /* ---------------- PREVENT DOUBLE BOOKING ---------------- */
  const isOverlapping = (n) =>
    bookings.some(b =>
      b.day === n.day &&
      n.startTime < b.endTime &&
      n.endTime > b.startTime
    )

  const handleSubmit = (e) => {
    e.preventDefault()

    if (isOverlapping(formData)) {
      alert('This time slot is already booked!')
      return
    }

    setBookings([...bookings, formData])

    setFormData({
      professor: '',
      department: '',
      school: '',
      day: '',
      startTime: '',
      endTime: '',
      room: '101'
    })
  }

  /* ---------------- GRID CLICK / DRAG AUTOFILL ---------------- */
  const handleCellSelect = (day, start, end) => {
    setFormData(prev => ({
      ...prev,
      day,
      startTime: start,
      endTime: end || prev.endTime,
      room: '101'
    }))
  }

  return (
    <>
      {/* ================= NAVBAR ================= */}
      <div className="navbar">
        <div className="nav-title">Conference Room Booking</div>

        <div className="nav-right">
          <span>Welcome {loggedInUser}</span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {/* ================= PAGE CONTENT ================= */}
      <main className="page-content">
        <div className="dashboard">

          {/* LEFT: BOOKING FORM */}
          <div className="card">
            <h3>Book Conference Room (101)</h3>

            <form onSubmit={handleSubmit} className="booking-form">
              <input
                name="professor"
                placeholder="Professor Name"
                value={formData.professor}
                onChange={handleChange}
                required
              />

              <input
                name="department"
                placeholder="Department"
                value={formData.department}
                onChange={handleChange}
                required
              />

              <input
                name="school"
                placeholder="School"
                value={formData.school}
                onChange={handleChange}
                required
              />

              <input value="Room 101" disabled />

              <select name="day" value={formData.day} onChange={handleChange} required>
                <option value="">Select Day</option>
                {['Monday','Tuesday','Wednesday','Thursday','Friday','Saturday','Sunday']
                  .map(d => <option key={d}>{d}</option>)}
              </select>

              <select name="startTime" value={formData.startTime} onChange={handleChange} required>
                <option value="">Start Time</option>
                {['08:00','09:00','09:50','10:40','11:30','12:20','14:00','15:00','16:00']
                  .map(t => <option key={t}>{t}</option>)}
              </select>

              <select name="endTime" value={formData.endTime} onChange={handleChange} required>
                <option value="">End Time</option>
                {['09:00','09:50','10:40','11:30','12:20','14:00','15:00','16:00','17:00']
                  .map(t => <option key={t}>{t}</option>)}
              </select>

              <button type="submit">Confirm Booking</button>
            </form>
          </div>

          {/* RIGHT: CALENDAR */}
          <div className="card">
            <h3>Weekly Schedule</h3>

            <CalendarView
              bookings={bookings}
              onBookingClick={setSelectedBooking}
              onCellSelect={handleCellSelect}
            />
          </div>
        </div>
      </main>

      {/* ================= BOOKING DETAILS POPUP ================= */}
      {selectedBooking && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Meeting Details</h3>

            <p><b>Professor:</b> {selectedBooking.professor}</p>
            <p><b>Department:</b> {selectedBooking.department}</p>
            <p><b>School:</b> {selectedBooking.school}</p>
            <p><b>Room:</b> 101</p>
            <p><b>Day:</b> {selectedBooking.day}</p>
            <p>
              <b>Time:</b> {selectedBooking.startTime} – {selectedBooking.endTime}
            </p>

            <button onClick={() => setSelectedBooking(null)}>Close</button>
          </div>
        </div>
      )}
    </>
  )
}

export default Home
